import React from "react";

const Game = props => {
  // const Cards = props => {
  //   return (
  //     <button className="cardBlock" onClick={props.onClick}>
  //       {props.imageone}
  //     </button>
  //   );
  // };

  const [game, setGame] = useState({
    cards: ["k", "j", "q", "a"],
    cardOneisNext: true,
    stepNumber: 0,
    history: [{ cards: Array(9).fill(null) }]
  });

  const flipCard = i => {
    const history = game.history.slice(0, game.stepNumber + 1);
    const current = history[history.length - 1];
    const cards = current.cards.slice();
    cards[i] = game.cardOneisNext ? "" : "JACK";
    setGame({
      history: history.concat({
        cards: cards
      }),
      cardOneisNext: !game.cardOneisNext,
      stepNumber: history.length
    });
  };

  const history = game.history;

  const current = history[game.stepNumber];

  return (
    <div className="game">
      <div className="game-board">
        <Board onClick={i => flipCard(i)} cards={current.cards} />
      </div>
    </div>
  );
};

export default Game;
