import React from "react";
import Presentation from "./presentation";

const Board = props => {
  const showCards = i => {
    return (
      <Presentation value={props.cards[i]} onClick={() => props.onClick(i)} />
    );
  };
  return (
    <div>
      <div className={"border-row"}>
        {showCards(0)}
        {showCards(1)}
        {showCards(2)}
      </div>
      <div className={"border-row"}>
        {showCards(3)}
        {showCards(4)}
        {showCards(5)}
      </div>
      <div className={"border-row"}>
        {showCards(6)}
        {showCards(7)}
        {showCards(8)}
      </div>
    </div>
  );
};

export default Board;
