import React from "react";

const Board = props => {
  const showCards = i => {
    props.cards.map(card => {
      return (
        <div>
          {card[i]} onClick={() => props.onClick(i)} />
        </div>
      );
    });
  };

  return (
    <React.Fragment>
      <h1 align="center">Memory-Game</h1>
      <div>
        <div className={"border-row"}>
          {showCards(0)}
          {showCards(1)}
          {showCards(0)}
          {showCards(1)}
        </div>
        <div className={"border-row"}>
          {showCards(0)}
          {showCards(1)}
          {showCards(0)}
          {showCards(1)}
        </div>
        <div className={"border-row"}>
          {showCards(0)}
          {showCards(1)}
          {showCards(0)}
          {showCards(1)}
        </div>
      </div>
    </React.Fragment>
  );
};

export default Board;
