import React, { useState } from "react";

const Cards = props => {
  const [card, setCard] = useState({
    cardClick: 0,
    cardMatch: 0
  });

  const actionHandler = event => {
    const clickHandler = event.target.value;
    setCard({ ...card, cardClick: clickHandler });
  };

  return (
    <button
      className="cardBlock"
      onClick={actionHandler}
      value={card.cardClick}
    >
      {props.value}
    </button>
  );
};

export default Cards;
